//let UNKNOWN = [ '#000088', '#000055' ]; // Blue
let UNKNOWN = [ '#888800', '#555500' ]; // Yellow
let MISSING = [ '#000000', '#000000' ]; // Black
let mqttStatus = MISSING;

if (numberOfConnections === connections.length) {
    if (connections.every(connection => connection.state === "connected")) {
        node.warn("Connections status: SUCCESS - #Connections: "+connections.length);
        // Store the RPE information for the dashboard
        // global.set('RPE-Name',connections[connections.length-1].name.substring(0, 6));
        mqttStatus = SUCCESS;
    } else {
        if (connections.some(connection => connection.state === "connected")) {
            node.warn("Connections status: UNKNOWN - #Connections: "+connections.length);
            mqttStatus = UNKNOWN;
        } else {
            node.warn("Connections status: ERROR - #Connections: "+connections.length);
            mqttStatus = ERROR;
        }
    }
} else {
    node.warn("Connections status: MISSING - #Connections: "+connections.length);
    mqttStatus = MISSING;

}

global.set('mqttStatusGlobal', mqttStatus);

