// Create the bar for the SenseHAT LEDs at a given row based on the given value
function bar(row, value){
    // Defining the main colors
    let G = '#005500'; // Green
    let R = '#550000'; // Red
    let Y = '#555500'; // Yellow
    let N = '#000000'; // N = No color => Black
    let LEDs = '';
    switch (true) {
        case (value < 20):
            LEDs = row+",5,"+G+","+row+",4,"+N+","+row+",3,"+N+","+row+",2,"+N+","+row+",1,"+N;
            break;
        case (value < 40):
            LEDs = row+",5,"+G+","+row+",4,"+G+","+row+",3,"+N+","+row+",2,"+N+","+row+",1,"+N;
            break;
        case (value < 60):
            LEDs = row+",5,"+G+","+row+",4,"+G+","+row+",3,"+G+","+row+",2,"+N+","+row+",1,"+N;
            break;
        case (value < 80):
            LEDs = row+",5,"+G+","+row+",4,"+G+","+row+",3,"+G+","+row+",2,"+Y+","+row+",1,"+N;
            break;
        default:
            LEDs = row+",5,"+G+","+row+",4,"+G+","+row+",3,"+G+","+row+",2,"+Y+","+row+",1,"+R;
            break;
    }
    return LEDs;
}

// The bars will be at 0,2,4,6
